mapper.select:
Select a.*,b.name as receiveUserName,c.name as insertUserName
From oilCardStock a
left join sys_user b on a.receive_user=b.id
left join sys_user c on a.insert_user=c.id

	
		and a.del_flag = #{delFlag} 
	
	
		and a.type = #{type} 
	
	
		and a.source like '%'+#{source}+'%'
	
	
		and a.card_type = #{cardType} 
	
	
		and a.card_no like '%'+#{cardNo}+'%'
	
	
		and a.card_no = #{cardNoCheck}
	
	
		and a.status = #{status} 
	
	
		and convert(char(10), a.insert_time,120) >= convert(char(10), #{startTime},120)
	
	
		and convert(char(10), a.insert_time,120) <= convert(char(10), #{endTime},120)
	
	
	    and b.name like '%'+#{receiveUserName}+'%'
	
	
		and convert(char(10), a.receive_time,120) >= convert(char(10), #{receiveStartTime},120)
	
	
		and convert(char(10), a.receive_time,120) <= convert(char(10), #{receiveEndTime},120)
	
	
	    and a.receive_user = #{receiveUser}
	

order by a.id desc

  	SELECT * from (
		SELECT row_number() over(order by a.id desc) as rownumber,
		a.*,b.name as receiveUserName,c.name as insertUserName
		From oilCardStock a
		left join sys_user b on a.receive_user=b.id
		left join sys_user c on a.insert_user=c.id
	
		
			and a.del_flag = #{delFlag} 
		
		
			and a.type = #{type} 
		
		
			and a.source like '%'+#{source}+'%'
		
		
			and a.card_type = #{cardType} 
		
		
			and a.card_no like '%'+#{cardNo}+'%'
		
		
			and a.status = #{status} 
		
		
			and convert(char(10), a.insert_time,120) >= convert(char(10), #{startTime},120)
		
		
			and convert(char(10), a.insert_time,120) <= convert(char(10), #{endTime},120)
		
		
		    and b.name like '%'+#{receiveUserName}+'%'
		
		
			and convert(char(10), a.receive_time,120) >= convert(char(10), #{receiveStartTime},120)
		
		
			and convert(char(10), a.receive_time,120) <= convert(char(10), #{receiveEndTime},120)
		
	
	) as t 
	WHERE t.rownumber > #{pageStartIndex} And t.rownumber <= #{pageEndIndex}
	ORDER BY t.id desc

SELECT count(*) FROM oilCardStock a left join sys_user b on a.receive_user=b.id left join sys_user c on a.insert_user=c.id
and a.del_flag = #{delFlag}
and a.type = #{type}
and a.source like '%'+#{source}+'%'
and a.card_type = #{cardType}
and a.card_no like '%'+#{cardNo}+'%'
and a.status = #{status}
and convert(char(10), a.insert_time,120) >= convert(char(10), #{startTime},120)
and convert(char(10), a.insert_time,120) <= convert(char(10), #{endTime},120)
and b.name like '%'+#{receiveUserName}+'%'
and convert(char(10), a.receive_time,120) >= convert(char(10), #{receiveStartTime},120)
and convert(char(10), a.receive_time,120) <= convert(char(10), #{receiveEndTime},120)
